insert into PRODUCTS (PROD_ID, ASIN, TITLE, ACTOR, PRICE, IMAGE_URL, DESCRIPTION) values ('53', 'B000EQHXNW', 'Match Point', 'Scarlett Johansson', 29.99, 'http://images.amazon.com/images/P/B000EQHXNW.01.MZZZZZZZ.jpg', 'The passion of mad love and the cold calculations of social climbing collide in Woody Allen''s Match Point. Former tennis pro Chris Wilton (Jonathan Rhys-Meyers, Velvet Goldmine) stumbles into good fortune when Chloe Hewett (Emily Mortimer, Lovely & Amazing), the daughter of a wealthy businessman, falls in love with him. But when Chris meets Nola Rice (Scarlett Johansson, Lost in Translation), a much deeper passion is stirred');
