insert into PRODUCTS (PROD_ID, ASIN, TITLE, ACTOR, PRICE, IMAGE_URL, DESCRIPTION) values ('136', 'B000DWMN2S', 'Everything Is Illuminated', 'Elijah Wood', 27.98, 'http://images.amazon.com/images/P/B000DWMN2S.01.MZZZZZZZ.jpg', 'Based on the novel by Jonathan Safran Foer, Everything is Illuminated stars Elijah Wood (Lord of the Rings) as Jonathan Safran Foer, a young Jewish man who wants to learn how his grandfather escaped from the Nazi incursions into Russia. From the U.S., he hires the hip-hop loving Alex (Eugene Hutz, leader of the gypsy-punk band Gogol Bordello) and his surly grandfather (Boris Leskin, Men in Black) as tour guides');
